options.getOption:
function getColorBasedOnSpeed(speed) {
  if (speed >= 0 && speed < 20) return 'lightgreen';
  else if (speed >= 20 && speed < 40) return 'green';
  else if (speed >= 40 && speed < 60) return 'yellow';
  else if (speed >= 60 && speed < 80) return 'orange';
  else if (speed >= 80 && speed < 100) return 'red';
  else if (speed >= 100) return 'darkred';
  else return 'gray';
}

const legendData = [
  { name: '0-20 km/h', color: 'lightgreen' },
  { name: '20-40 km/h', color: 'green' },
  { name: '40-60 km/h', color: 'yellow' },
  { name: '60-80 km/h', color: 'orange' },
  { name: '80-100 km/h', color: 'red' },
  { name: '>100 km/h', color: 'darkred' },
];

const legendSeries = legendData.map((item) => {
  return {
    name: item.name,
    type: 'line',
    lineStyle: { width: 0 }, // hide the line
    symbolSize: 0, // hide the symbol
    itemStyle: {
      color: item.color
    },
    data: [] // no actual data
  };
});

const series = data.series.map((s) => {
  const sData = s.fields.find((f, index) => index === 0).values.buffer;
  const sTime = s.fields.find((f, index) => index === 1).values.buffer;
  const sAngle = s.fields.find((f, index) => index === 0).values.buffer;
  const sSpeed = s.fields.find((f, index) => index === 2).values.buffer;

  return {
    name: s.refId,
    type: 'line',
    showSymbol: true,
    symbol: "path://m13.022 14.999v3.251c0 .412.335.75.752.75.188 0 .375-.071.518-.206 1.775-1.685 4.945-4.692 6.396-6.069.2-.189.312-.452.312-.725 0-.274-.112-.536-.312-.725-1.451-1.377-4.621-4.385-6.396-6.068-.143-.136-.33-.207-.518-.207-.417 0-.752.337-.752.75v3.251h-9.02c-.531 0-1.002.47-1.002 1v3.998c0 .53.471 1 1.002 1z",
    symbolSize: 15,
    areaStyle: {
      opacity: 0.1,
    },
    lineStyle: {
      width: 1,
    },
    data: sData.map((d, i) => {
      let adjustedAngle = sAngle[i] + 90;
      if (adjustedAngle >= 360) adjustedAngle -= 360;
      return {
        value: [sTime[i], sSpeed[i]], // changed from 'd' to 'sSpeed[i]'
        symbolRotate: adjustedAngle,
        itemStyle: {
          color: getColorBasedOnSpeed(sSpeed[i])
        }
      };
    }),
  };
});

setTimeout(() => echartsInstance.dispatchAction({
  type: 'takeGlobalCursor',
  key: 'dataZoomSelect',
  dataZoomSelectActive: true,
}), 500);

echartsInstance.on('datazoom', function (params) {
  const startValue = params.batch[0]?.startValue;
  const endValue = params.batch[0]?.endValue;
  locationService.partial({ from: startValue, to: endValue });
});

return {
  backgroundColor: 'transparent',
  tooltip: {
    trigger: 'axis',
  },
  legend: {
    orient: 'horizontal',
    left: 'centre',
    bottom: '1%', // Adjust this value as needed to position the legend
    data: legendData.map((item) => item.name),
    textStyle: {
      color: 'rgba(128, 128, 128, .9)',
    },
  },
  toolbox: {
    feature: {
      dataZoom: {
        yAxisIndex: 'none',
        icon: {
          zoom: 'path://',
          back: 'path://',
        },
      },
      saveAsImage: {},
    }
  },
  xAxis: {
    type: 'time',
  },
  yAxis: {
    type: 'value',
    min: 'dataMin',
  },
  grid: {
    left: '2%',
    right: '2%',
    top: '2%',
    bottom: '25%',
    containLabel: true,
  },
  series: [...series, ...legendSeries], // include the legend series in the series data
};
